import React, { useState } from 'react';
import { useTranslation } from 'react-i18next';
import { submitBusinessInquiry, checkApiHealth } from '../services/api';

const defaultFormState = {
  ownerName: '',
  email: '',
  phone: '',
  businessName: '',
  businessType: '',
  locations: '',
  website: '',
  message: '',
};

const BusinessContact = () => {
  const { t } = useTranslation();
  const [formState, setFormState] = useState(defaultFormState);
  const [status, setStatus] = useState({ type: 'idle', message: '' });
  const [health, setHealth] = useState(null);

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormState((prev) => ({ ...prev, [name]: value }));
  };

  const resetStatus = () => setStatus({ type: 'idle', message: '' });

  const handleSubmit = async (event) => {
    event.preventDefault();
    setStatus({ type: 'loading', message: t('businessContact.status.submitting') });

    try {
      const payload = {
        ownerName: formState.ownerName.trim(),
        email: formState.email.trim(),
        phone: formState.phone.trim(),
        businessName: formState.businessName.trim(),
        businessType: formState.businessType.trim(),
        locations: formState.locations.trim(),
        website: formState.website.trim(),
        message: formState.message.trim(),
      };

      const result = await submitBusinessInquiry(payload);

      setStatus({
        type: 'success',
        message: t('businessContact.status.success', { referenceId: result.referenceId }),
      });
      setFormState(defaultFormState);
    } catch (error) {
      setStatus({
        type: 'error',
        message: error.message || t('businessContact.status.error'),
      });
    }
  };

  const handleHealthCheck = async () => {
    resetStatus();
    try {
      const apiHealth = await checkApiHealth();
      setHealth(apiHealth);
    } catch (error) {
      setHealth({ status: 'unreachable', message: error.message });
    }
  };

  return (
    <section
      className="relative py-24 bg-gradient-to-b from-gray-50 to-white dark:from-gray-900 dark:to-gray-950 transition-colors"
      data-testid="business-contact-page"
    >
      <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="grid lg:grid-cols-2 gap-12">
          <div className="space-y-6">
            <div className="space-y-4">
              <p className="text-sm font-semibold tracking-widest text-primary-600 uppercase">
                {t('businessContact.badge')}
              </p>
              <h1 className="text-4xl sm:text-5xl font-bold text-gray-900 dark:text-white">
                {t('businessContact.title')}
              </h1>
              <p className="text-lg text-gray-600 dark:text-gray-300">
                {t('businessContact.subtitle')}
              </p>
            </div>

            <div className="grid sm:grid-cols-2 gap-4">
              {['support', 'sla'].map((cardKey) => (
                <div key={cardKey} className="p-5 rounded-2xl border border-gray-200 dark:border-gray-800 bg-white dark:bg-gray-900 shadow-sm">
                  <h3 className="text-sm font-semibold text-primary-600 uppercase tracking-wide">
                    {t(`businessContact.cards.${cardKey}.title`)}
                  </h3>
                  <p className="mt-2 text-gray-900 dark:text-white text-lg font-bold">
                    {t(`businessContact.cards.${cardKey}.value`)}
                  </p>
                  <p className="mt-1 text-sm text-gray-500 dark:text-gray-400">
                    {t(`businessContact.cards.${cardKey}.description`)}
                  </p>
                </div>
              ))}
            </div>

            <div className="rounded-2xl border border-primary-100 dark:border-primary-900 bg-primary-50 dark:bg-primary-900/20 p-6 space-y-3">
              <h3 className="text-base font-semibold text-primary-700 dark:text-primary-300">
                {t('businessContact.integration.title')}
              </h3>
              <p className="text-sm text-primary-800 dark:text-primary-200">
                {t('businessContact.integration.description')}
              </p>
              <button
                onClick={handleHealthCheck}
                className="inline-flex items-center px-4 py-2 text-sm font-semibold text-white bg-primary-600 hover:bg-primary-700 rounded-lg transition-colors"
              >
                {t('businessContact.integration.cta')}
              </button>
              {health && (
                <div className="text-sm text-primary-900 dark:text-primary-100">
                  {t('businessContact.integration.health', { status: health.status })}
                  {health.message && ` • ${health.message}`}
                </div>
              )}
            </div>
          </div>

          <div className="bg-white dark:bg-gray-900 rounded-3xl shadow-xl border border-gray-100 dark:border-gray-800 p-8">
            <form className="space-y-6" onSubmit={handleSubmit}>
              <div className="space-y-2">
                <label htmlFor="ownerName" className="text-sm font-medium text-gray-700 dark:text-gray-300">
                  {t('businessContact.form.ownerName')}
                </label>
                <input
                  id="ownerName"
                  name="ownerName"
                  value={formState.ownerName}
                  onChange={handleChange}
                  required
                  className="w-full rounded-2xl border-gray-200 dark:border-gray-700 bg-gray-50 dark:bg-gray-800 text-gray-900 dark:text-white focus:ring-primary-500 focus:border-primary-500"
                />
              </div>

              <div className="grid sm:grid-cols-2 gap-4">
                <div className="space-y-2">
                  <label htmlFor="email" className="text-sm font-medium text-gray-700 dark:text-gray-300">
                    {t('businessContact.form.email')}
                  </label>
                  <input
                    id="email"
                    type="email"
                    name="email"
                    value={formState.email}
                    onChange={handleChange}
                    required
                    className="w-full rounded-2xl border-gray-200 dark:border-gray-700 bg-gray-50 dark:bg-gray-800 text-gray-900 dark:text-white focus:ring-primary-500 focus:border-primary-500"
                  />
                </div>
                <div className="space-y-2">
                  <label htmlFor="phone" className="text-sm font-medium text-gray-700 dark:text-gray-300">
                    {t('businessContact.form.phone')}
                  </label>
                  <input
                    id="phone"
                    type="tel"
                    name="phone"
                    value={formState.phone}
                    onChange={handleChange}
                    required
                    className="w-full rounded-2xl border-gray-200 dark:border-gray-700 bg-gray-50 dark:bg-gray-800 text-gray-900 dark:text-white focus:ring-primary-500 focus:border-primary-500"
                  />
                </div>
              </div>

              <div className="space-y-2">
                <label htmlFor="businessName" className="text-sm font-medium text-gray-700 dark:text-gray-300">
                  {t('businessContact.form.businessName')}
                </label>
                <input
                  id="businessName"
                  name="businessName"
                  value={formState.businessName}
                  onChange={handleChange}
                  required
                  className="w-full rounded-2xl border-gray-200 dark:border-gray-700 bg-gray-50 dark:bg-gray-800 text-gray-900 dark:text-white focus:ring-primary-500 focus:border-primary-500"
                />
              </div>

              <div className="grid sm:grid-cols-2 gap-4">
                <div className="space-y-2">
                  <label htmlFor="businessType" className="text-sm font-medium text-gray-700 dark:text-gray-300">
                    {t('businessContact.form.businessType')}
                  </label>
                  <input
                    id="businessType"
                    name="businessType"
                    value={formState.businessType}
                    onChange={handleChange}
                    required
                    className="w-full rounded-2xl border-gray-200 dark:border-gray-700 bg-gray-50 dark:bg-gray-800 text-gray-900 dark:text-white focus:ring-primary-500 focus:border-primary-500"
                  />
                </div>
                <div className="space-y-2">
                  <label htmlFor="locations" className="text-sm font-medium text-gray-700 dark:text-gray-300">
                    {t('businessContact.form.locations')}
                  </label>
                  <input
                    id="locations"
                    name="locations"
                    value={formState.locations}
                    onChange={handleChange}
                    placeholder={t('businessContact.form.locationsPlaceholder')}
                    className="w-full rounded-2xl border-gray-200 dark:border-gray-700 bg-gray-50 dark:bg-gray-800 text-gray-900 dark:text-white focus:ring-primary-500 focus:border-primary-500"
                  />
                </div>
              </div>

              <div className="space-y-2">
                <label htmlFor="website" className="text-sm font-medium text-gray-700 dark:text-gray-300">
                  {t('businessContact.form.website')}
                </label>
                <input
                  id="website"
                  type="url"
                  name="website"
                  value={formState.website}
                  onChange={handleChange}
                  placeholder="https://"
                  className="w-full rounded-2xl border-gray-200 dark:border-gray-700 bg-gray-50 dark:bg-gray-800 text-gray-900 dark:text-white focus:ring-primary-500 focus:border-primary-500"
                />
              </div>

              <div className="space-y-2">
                <label htmlFor="message" className="text-sm font-medium text-gray-700 dark:text-gray-300">
                  {t('businessContact.form.message')}
                </label>
                <textarea
                  id="message"
                  name="message"
                  rows={5}
                  value={formState.message}
                  onChange={handleChange}
                  required
                  className="w-full rounded-2xl border-gray-200 dark:border-gray-700 bg-gray-50 dark:bg-gray-800 text-gray-900 dark:text-white focus:ring-primary-500 focus:border-primary-500"
                />
              </div>

              <div className="space-y-3">
                <button
                  type="submit"
                  disabled={status.type === 'loading'}
                  className="w-full inline-flex justify-center items-center px-6 py-4 rounded-2xl text-white font-semibold bg-primary-600 hover:bg-primary-700 disabled:opacity-60 disabled:cursor-not-allowed transition-colors"
                >
                  {status.type === 'loading'
                    ? t('businessContact.form.ctaLoading')
                    : t('businessContact.form.cta')}
                </button>

                {status.message && (
                  <div
                    className={`text-sm font-medium ${
                      status.type === 'error'
                        ? 'text-red-600 dark:text-red-400'
                        : 'text-green-600 dark:text-green-400'
                    }`}
                  >
                    {status.message}
                  </div>
                )}
              </div>
            </form>
          </div>
        </div>
      </div>
    </section>
  );
};

export default BusinessContact;
