import React from 'react';
import { useTranslation } from 'react-i18next';
import { FiSmartphone } from 'react-icons/fi';

const DownloadSection = () => {
  const { t } = useTranslation();

  return (
    <section id="download" className="py-24 bg-gradient-to-br from-primary-600 to-primary-800 dark:from-primary-800 dark:to-primary-950 relative overflow-hidden" data-testid="download-section">
      {/* Decorative Elements */}
      <div className="absolute inset-0 opacity-10">
        <div className="absolute top-10 left-10 w-64 h-64 bg-white rounded-full blur-3xl"></div>
        <div className="absolute bottom-10 right-10 w-96 h-96 bg-white rounded-full blur-3xl"></div>
      </div>

      <div className="relative max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center space-y-8">
          {/* Icon */}
          <div className="inline-flex p-6 bg-white/10 backdrop-blur-sm rounded-3xl shadow-2xl">
            <FiSmartphone className="w-16 h-16 text-white" />
          </div>

          {/* Title */}
          <h2 className="text-4xl sm:text-5xl font-bold text-white" data-testid="download-title">
            {t('download.title')}
          </h2>

          {/* Subtitle */}
          <p className="text-xl text-white/90 max-w-3xl mx-auto leading-relaxed" data-testid="download-subtitle">
            {t('download.subtitle')}
          </p>

          {/* Download Buttons */}
          <div className="flex flex-col sm:flex-row items-center justify-center gap-4 pt-4">
            <button className="group px-8 py-4 bg-white hover:bg-gray-100 text-primary-600 font-bold rounded-xl transition-all transform hover:scale-105 shadow-xl hover:shadow-2xl flex items-center space-x-3" data-testid="download-cta">
              <svg className="w-8 h-8" viewBox="0 0 24 24" fill="currentColor">
                <path d="M17.05 20.28c-.98.95-2.05.8-3.08.35-1.09-.46-2.09-.48-3.24 0-1.44.62-2.2.44-3.06-.35C2.79 15.25 3.51 7.59 9.05 7.31c1.35.07 2.29.74 3.08.8 1.18-.24 2.31-.93 3.57-.84 1.51.12 2.65.72 3.4 1.8-3.12 1.87-2.38 5.98.48 7.13-.57 1.5-1.31 2.99-2.54 4.09l.01-.01zM12.03 7.25c-.15-2.23 1.66-4.07 3.74-4.25.29 2.58-2.34 4.5-3.74 4.25z"/>
              </svg>
              <div className="text-left">
                <div className="text-xs text-gray-600">{t('download.comingSoon')}</div>
                <div className="text-sm font-bold">App Store</div>
              </div>
            </button>

            <button className="group px-8 py-4 bg-white hover:bg-gray-100 text-primary-600 font-bold rounded-xl transition-all transform hover:scale-105 shadow-xl hover:shadow-2xl flex items-center space-x-3" data-testid="download-cta-android">
              <svg className="w-8 h-8" viewBox="0 0 24 24" fill="currentColor">
                <path d="M3,20.5V3.5C3,2.91 3.34,2.39 3.84,2.15L13.69,12L3.84,21.85C3.34,21.6 3,21.09 3,20.5M16.81,15.12L6.05,21.34L14.54,12.85L16.81,15.12M20.16,10.81C20.5,11.08 20.75,11.5 20.75,12C20.75,12.5 20.53,12.9 20.18,13.18L17.89,14.5L15.39,12L17.89,9.5L20.16,10.81M6.05,2.66L16.81,8.88L14.54,11.15L6.05,2.66Z"/>
              </svg>
              <div className="text-left">
                <div className="text-xs text-gray-600">{t('download.comingSoon')}</div>
                <div className="text-sm font-bold">Google Play</div>
              </div>
            </button>
          </div>
        </div>
      </div>
    </section>
  );
};

export default DownloadSection;
