import React, { useState } from 'react';
import { useTranslation } from 'react-i18next';
import { submitBusinessInquiry, checkApiHealth } from '../services/api';
import { FiCheckCircle, FiClock, FiServer } from 'react-icons/fi';

const defaultFormState = {
  companyName: '',
  email: '',
  phone: '',
  tin: '',
  password: '',
  repeatPassword: '',
};

const BusinessContact = () => {
  const { t } = useTranslation();
  const [formState, setFormState] = useState(defaultFormState);
  const [status, setStatus] = useState({ type: 'idle', message: '' });
  const [health, setHealth] = useState(null);

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormState((prev) => ({ ...prev, [name]: value }));
  };

  const resetStatus = () => setStatus({ type: 'idle', message: '' });

  const handleSubmit = async (event) => {
    event.preventDefault();
    setStatus({ type: 'loading', message: t('businessContact.status.submitting') });

    try {
      if (formState.password !== formState.repeatPassword) {
        setStatus({
          type: 'error',
          message: t('businessContact.status.passwordMismatch'),
        });
        return;
      }

      const payload = {
        companyName: formState.companyName.trim(),
        email: formState.email.trim(),
        phone: formState.phone.trim(),
        tin: formState.tin.trim(),
        password: formState.password,
        repeatPassword: formState.repeatPassword,
      };

      const result = await submitBusinessInquiry(payload);

      setStatus({
        type: 'success',
        message: t('businessContact.status.success', { referenceId: result.referenceId }),
      });
      setFormState(defaultFormState);
    } catch (error) {
      setStatus({
        type: 'error',
        message: error.message || t('businessContact.status.error'),
      });
    }
  };

  const handleHealthCheck = async () => {
    resetStatus();
    try {
      const apiHealth = await checkApiHealth();
      setHealth(apiHealth);
    } catch (error) {
      setHealth({ status: 'unreachable', message: error.message });
    }
  };

  const cardConfigs = [
    { key: 'support', icon: FiCheckCircle, gradient: 'from-green-500 to-emerald-500' },
    { key: 'sla', icon: FiClock, gradient: 'from-blue-500 to-cyan-500' },
  ];

  return (
    <section
      className="relative py-24 bg-gradient-to-b from-gray-50 to-white dark:from-gray-900 dark:to-gray-950 transition-colors"
      data-testid="business-contact-page"
    >
      <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="grid lg:grid-cols-2 gap-12 lg:gap-16">
          {/* Left Column */}
          <div className="space-y-8">
            {/* Header Section */}
            <div className="space-y-4 animate-fade-in">
              <div className="inline-flex items-center px-4 py-2 rounded-full bg-primary-100 dark:bg-primary-900/30 border border-primary-200 dark:border-primary-800">
                <p className="text-sm font-semibold tracking-widest text-primary-600 dark:text-primary-400 uppercase">
                  {t('businessContact.badge')}
                </p>
              </div>
              <h1 className="text-4xl sm:text-5xl font-bold text-gray-900 dark:text-white leading-tight">
                {t('businessContact.title')}
              </h1>
              <p className="text-lg text-gray-600 dark:text-gray-300 leading-relaxed">
                {t('businessContact.subtitle')}
              </p>
            </div>

            {/* Feature Cards */}
            <div className="grid sm:grid-cols-2 gap-4">
              {cardConfigs.map(({ key, icon: Icon, gradient }) => (
                <div 
                  key={key} 
                  className="group relative p-6 rounded-2xl border border-gray-200 dark:border-gray-800 bg-white dark:bg-gray-900 shadow-lg hover:shadow-2xl transition-all duration-300 hover:-translate-y-1"
                >
                  <div className={`absolute inset-0 bg-gradient-to-br ${gradient} opacity-0 group-hover:opacity-5 rounded-2xl transition-opacity duration-300`}></div>
                  <div className="relative">
                    <div className={`inline-flex p-3 bg-gradient-to-br ${gradient} rounded-xl shadow-lg mb-4`}>
                      <Icon className="w-5 h-5 text-white" />
                    </div>
                    <h3 className="text-xs font-semibold text-primary-600 dark:text-primary-400 uppercase tracking-wider mb-2">
                      {t(`businessContact.cards.${key}.title`)}
                    </h3>
                    <p className="text-gray-900 dark:text-white text-xl font-bold mb-2">
                      {t(`businessContact.cards.${key}.value`)}
                    </p>
                    <p className="text-sm text-gray-500 dark:text-gray-400 leading-relaxed">
                      {t(`businessContact.cards.${key}.description`)}
                    </p>
                  </div>
                </div>
              ))}
            </div>

            {/* API Integration Card */}
            <div className="group relative rounded-2xl border border-primary-200 dark:border-primary-800 bg-gradient-to-br from-primary-50 to-primary-100/50 dark:from-primary-900/20 dark:to-primary-900/10 p-6 space-y-4 shadow-lg hover:shadow-xl transition-all duration-300">
              <div className="flex items-start space-x-3">
                <div className="p-2 bg-primary-600 rounded-lg">
                  <FiServer className="w-5 h-5 text-white" />
                </div>
                <div className="flex-1 space-y-2">
                  <h3 className="text-lg font-semibold text-primary-900 dark:text-primary-100">
                    {t('businessContact.integration.title')}
                  </h3>
                  <p className="text-sm text-primary-800 dark:text-primary-200 leading-relaxed">
                    {t('businessContact.integration.description')}
                  </p>
                </div>
              </div>
              <button
                onClick={handleHealthCheck}
                className="w-full inline-flex items-center justify-center px-4 py-3 text-sm font-semibold text-white bg-primary-600 hover:bg-primary-700 rounded-xl transition-all transform hover:scale-[1.02] shadow-md hover:shadow-lg"
              >
                <FiServer className="w-4 h-4 mr-2" />
                {t('businessContact.integration.cta')}
              </button>
              {health && (
                <div className="mt-3 p-3 rounded-lg bg-white/60 dark:bg-gray-800/60 backdrop-blur-sm border border-primary-200 dark:border-primary-700">
                  <p className="text-sm font-medium text-primary-900 dark:text-primary-100">
                    {t('businessContact.integration.health', { status: health.status })}
                    {health.message && <span className="block mt-1 text-xs text-primary-700 dark:text-primary-300">{health.message}</span>}
                  </p>
                </div>
              )}
            </div>
          </div>

          <div className="bg-white dark:bg-gray-900 rounded-3xl shadow-xl border border-gray-100 dark:border-gray-800 p-8">
            <form className="space-y-6" onSubmit={handleSubmit}>
              <div className="space-y-2">
                <label htmlFor="companyName" className="text-sm font-medium text-gray-700 dark:text-gray-300">
                  {t('businessContact.form.companyName')}
                </label>
                <input
                  id="companyName"
                  name="companyName"
                  value={formState.companyName}
                  onChange={handleChange}
                  required
                  className="w-full rounded-2xl border-gray-200 dark:border-gray-700 bg-gray-50 dark:bg-gray-800 text-gray-900 dark:text-white focus:ring-primary-500 focus:border-primary-500"
                />
              </div>

              <div className="grid sm:grid-cols-2 gap-4">
                <div className="space-y-2">
                  <label htmlFor="email" className="text-sm font-medium text-gray-700 dark:text-gray-300">
                    {t('businessContact.form.email')}
                  </label>
                  <input
                    id="email"
                    type="email"
                    name="email"
                    value={formState.email}
                    onChange={handleChange}
                    required
                    className="w-full rounded-2xl border-gray-200 dark:border-gray-700 bg-gray-50 dark:bg-gray-800 text-gray-900 dark:text-white focus:ring-primary-500 focus:border-primary-500"
                  />
                </div>
                <div className="space-y-2">
                  <label htmlFor="phone" className="text-sm font-medium text-gray-700 dark:text-gray-300">
                    {t('businessContact.form.phone')}
                  </label>
                  <input
                    id="phone"
                    type="tel"
                    name="phone"
                    value={formState.phone}
                    onChange={handleChange}
                    required
                    className="w-full rounded-2xl border-gray-200 dark:border-gray-700 bg-gray-50 dark:bg-gray-800 text-gray-900 dark:text-white focus:ring-primary-500 focus:border-primary-500"
                  />
                </div>
              </div>

              <div className="space-y-2">
                <label htmlFor="tin" className="text-sm font-medium text-gray-700 dark:text-gray-300">
                  {t('businessContact.form.tin')}
                </label>
                <input
                  id="tin"
                  name="tin"
                  value={formState.tin}
                  onChange={handleChange}
                  required
                  className="w-full rounded-2xl border-gray-200 dark:border-gray-700 bg-gray-50 dark:bg-gray-800 text-gray-900 dark:text-white focus:ring-primary-500 focus:border-primary-500"
                />
              </div>

              <div className="grid sm:grid-cols-2 gap-4">
                <div className="space-y-2">
                  <label htmlFor="password" className="text-sm font-medium text-gray-700 dark:text-gray-300">
                    {t('businessContact.form.password')}
                  </label>
                  <input
                    id="password"
                    type="password"
                    name="password"
                    value={formState.password}
                    onChange={handleChange}
                    required
                    className="w-full rounded-2xl border-gray-200 dark:border-gray-700 bg-gray-50 dark:bg-gray-800 text-gray-900 dark:text-white focus:ring-primary-500 focus:border-primary-500"
                  />
                </div>
                <div className="space-y-2">
                  <label htmlFor="repeatPassword" className="text-sm font-medium text-gray-700 dark:text-gray-300">
                    {t('businessContact.form.repeatPassword')}
                  </label>
                  <input
                    id="repeatPassword"
                    type="password"
                    name="repeatPassword"
                    value={formState.repeatPassword}
                    onChange={handleChange}
                    required
                    className="w-full rounded-2xl border-gray-200 dark:border-gray-700 bg-gray-50 dark:bg-gray-800 text-gray-900 dark:text-white focus:ring-primary-500 focus:border-primary-500"
                  />
                </div>
              </div>

              <div className="space-y-3">
                <button
                  type="submit"
                  disabled={status.type === 'loading'}
                  className="w-full inline-flex justify-center items-center px-6 py-4 rounded-2xl text-white font-semibold bg-primary-600 hover:bg-primary-700 disabled:opacity-60 disabled:cursor-not-allowed transition-colors"
                >
                  {status.type === 'loading'
                    ? t('businessContact.form.ctaLoading')
                    : t('businessContact.form.cta')}
                </button>

                {status.message && (
                  <div
                    className={`text-sm font-medium ${
                      status.type === 'error'
                        ? 'text-red-600 dark:text-red-400'
                        : 'text-green-600 dark:text-green-400'
                    }`}
                  >
                    {status.message}
                  </div>
                )}
              </div>
            </form>
          </div>
        </div>
      </div>
    </section>
  );
};

export default BusinessContact;
